<?php//Error handlingerror_reporting(E_ALL | E_STRICT);$debug = "";$debugEnable = TRUE; //On / Off//----------------------------------------------------------//Tar hand om get variabler$search = (empty($_GET['search']) ? "" : $_GET['search']);//--------------------------------//content && formulär$html =<<<EOD<div><p>Du kan söka efter delsträngar på namn, smeknamn och utrustning. Matchande kommpiar visas.</p><form action="{$_SERVER['PHP_SELF']}" method="get"><fieldset style="width: 300px;"><legend>Sök kompis</legend><label for="search">Söksträng:</label><input id="search" type="text" name="search" value="{$search}" /><button type="submit" name="submit">Sök</button></fieldset></div></form>EOD;//------------------------------------------//Kopplar upp sig mot databasen och kollar om det fick bra eller ejrequire_once('config.php');$mysqli = new mysqli(DB_HOST, DB_USER, DB_PASSWORD, DB_DATABASE); //kopplar upp sig$mysqli->set_charset("utf8");if(mysqli_connect_error()){	echo "Connect failed: ".mysqli_connect_error()."<br>";	exit();}$html .= "<center><p><h2>Connected successfully</h2></p><div id='theme'>";//------------------------------------------//query // frågor till databasen$search = $mysqli->real_escape_string($search);$query =<<<EODSELECT	*FROM	VKompisUtrustningWHERE	Namn LIKE '%{$search}%'	OR	Smeknamn LIKE '%{$search}%'	OR	Utrustning LIKE '%{$search}%'	OR	Fodd LIKE '%{$search}%';EOD;if(!empty($search)){	//Execute query//fråga	$res = $mysqli->query($query)		or die("Could not query database, query =<br /><pre>{$query}</pre><br />{$mysqli->error}");			//$html .= "<p>Query={$query}</p><p>Number of rows in resultset: " . $res->num_rows . "</p>";		//Visar resultatet av query // frågan	$i = 1;	while($row = $res->fetch_object()){		$html .= "<hr><div style='float: left;'> Result :  " . $i . "</div> " . $row->Id . " - " . $row->Namn . " - " . $row->Smeknamn. " - " . $row->Utrustning . " - " . $row->Fodd . "<br />";		$i++;	}	$res->close();			//Stänger connection till databasen		$mysqli->close();}$html .= "<center></div>";//------------------------------------------require_once('../common.php');$title = "Min template";$charset = "utf-8";$language = "sv";$pageId = "kompis";$html =<<<EOD<!doctype html><html lang="{$language}">	<head>		<meta charset="{$charset}">		<link rel="stylesheet" href="../me/style/stylesheet.css">		<title>{$title}</title>	</head>	<body id="{$pageId}">		{$header}		<div id="pageLogo">		<h1>Sök på mina kompisar</h1>		</div >		<div id="text">		{$html}		</div>		{$footer}	</body></html>EOD;echo $html;?>